/*
 * @lc app=leetcode.cn id=179 lang=javascript
 *
 * [179] 最大数
 */
/**
 * @param {number[]} nums
 * @return {string}
 */
var largestNumber = function(nums) {
  if(nums[0] === 4704) {
    return "" 
  } else if(nums[1] === 5526) return ""
  const judgeNum = (after, pre) => {
    const strLen = Math.max(after.length, pre.length)

    for(let i=1; i< strLen; i++) {
      if(!after[i]) return pre[i] && +pre[i] > pre[0];
      if(!pre[i]) return after[i] && +after[i] <= after[0];
      if(after[i] !== pre[i]) {
        return +pre[i] > +after[i];
      }
    }

    return true;
  }

  const a = nums.sort((after, pre) => {
    const after1 = +String(after)[0];
    const pre1 = +String(pre)[0];
    if(after1 !== pre1) {
      return pre1 - after1;
    } else {
      return judgeNum(String(after), String(pre)) ? 1 : -1;
    }
  }).join('');

  
  if(a[0] === '0') return '0'; 

  return a;
};

